mapper.select:
select count(*)
from (
select crt_username
, if(A.date_day<>B.date_day,'',sign_in_time) sign_in_time 
, if(A.date_day<>B.date_day,'',sign_out_time) sign_out_time 
from (
select crt_username,date_format(crt_time,'%Y-%m-%d') date_day
,substring_index(group_concat(crt_time order by id separator
'|'),'|',1) sign_in_time
,if(count(*)=1,'',substring_index(group_concat(crt_time order by id
separator '|'),'|',-1)) sign_out_time
from attendance_info


		and crt_username like concat('%',#{account},'%')


		and crt_user_name like concat('%',#{name},'%')


		and depart_id in(#{depart_id})


					 and crt_time >= #{startDate} 


					 and crt_time < #{endDate} 


group by crt_username,date_format(crt_time,'%Y-%m-%d')
order by
date_day
) A , date_info B


			 and B.date_day >= #{startDate} 


			 and B.date_day < #{endDate} 


) C


		 and sign_in_time <> '' and sign_out_time <> '' 


		and (sign_in_time = '' or sign_out_time = '')

select * from ( select crt_username,B.date_day,crt_user_name,depart_id , if(A.date_day<>B.date_day,'',sign_in_time) sign_in_time , if(A.date_day<>B.date_day,'',sign_out_time) sign_out_time , if(A.date_day<>B.date_day,'',sign_in_map_info) sign_in_map_info , if(A.date_day<>B.date_day,'',sign_out_map_info) sign_out_map_info , if(A.date_day<>B.date_day,'',sign_in_map) sign_in_map , if(A.date_day<>B.date_day,'',sign_out_map) sign_out_map from ( select crt_username,date_format(crt_time,'%Y-%m-%d') date_day ,substring_index(group_concat(crt_user_name order by id separator '|'),'|',1) crt_user_name ,substring_index(group_concat(depart_id order by id separator '|'),'|',1) depart_id ,substring_index(group_concat(DATE_FORMAT(crt_time,'%H:%i:%s') order by id separator '|'),'|',1) sign_in_time ,if(count(*)=1,'',substring_index(group_concat(DATE_FORMAT(crt_time,'%H:%i:%s') order by id separator '|'),'|',-1)) sign_out_time ,substring_index(group_concat(map_info order by id separator '|'),'|',1) sign_in_map_info ,if(count(*)=1,'',substring_index(group_concat(map_info order by id separator '|'),'|',-1)) sign_out_map_info ,if(count(*)=1,'',substring_index(group_concat(longitude,',',latitude order by id separator '|'),'|',1)) sign_in_map ,if(count(*)=1,'',substring_index(group_concat(longitude,',',latitude order by id separator '|'),'|',-1)) sign_out_map from attendance_info
and crt_username like concat('%',#{account},'%')
and crt_user_name like concat('%',#{name},'%')
and depart_id in(#{depart_id})
and crt_time >= #{startDate}
and crt_time < #{endDate}
group by crt_username,date_format(crt_time,'%Y-%m-%d') order by date_day ) A , date_info B
and B.date_day >= #{startDate}
and B.date_day < #{endDate}
) C
and sign_in_time <> '' and sign_out_time <> ''
and (sign_in_time = '' or sign_out_time = '')
order by date_day,crt_username limit ${startIndex},${limit}
