biz_list

SELECT id, crt_time crtTime, case_title caseTitle, case_level caseLevel, is_supervise isSupervise, is_urge isUrge FROM case_info WHERE is_deleted = 0 AND is_finished = 0 AND execute_dept = #{deptId}
AND case_level = #{caseLevel}
ORDER BY crt_time DESC

SELECT COUNT(*) ccount,zfqpName,zfqpLevel,zfqp FROM ( SELECT CASE WHEN gridLevel=#{gridLevel} THEN gridId WHEN parentGridLevel=#{gridLevel} THEN gridParent WHEN grandParentGridLevel=#{gridLevel} THEN gridGrandParent END zfqp, CASE WHEN gridLevel=#{gridLevel} THEN gridLevel WHEN parentGridLevel=#{gridLevel} THEN parentGridLevel WHEN grandParentGridLevel=#{gridLevel} THEN grandParentGridLevel END zfqpLevel, CASE WHEN gridLevel=#{gridLevel} THEN gridName WHEN parentGridLevel=#{gridLevel} THEN gridParentName WHEN grandParentGridLevel=#{gridLevel} THEN gridGrandParentName END zfqpName FROM(
SELECT ci.`id` caseId,gridId,gridLevel,gridParent,gridName,parentGridLevel,gridGrandParent,gridParentName,grandParentGridLevel,gridGrandGrandParent,gridGrandParentName FROM case_info ci LEFT JOIN ( SELECT gridId,gridLevel,gridParent,gridName,parentGridLevel,gridGrandParent,gridParentName,ag3.grid_level grandParentGridLevel,ag3.`grid_parent` gridGrandGrandParent,ag3.grid_name gridGrandParentName FROM(
SELECT gridId,gridLevel,gridParent,ag2.`grid_parent` gridGrandParent,ag2.grid_name gridParentName,ag2.grid_level parentGridLevel,ag2.id id2,gridName FROM (
SELECT ag.`id` gridId,ag.`grid_level` gridLevel,ag.`grid_parent` gridParent,ag.`grid_name` gridName FROM area_grid ag WHERE is_deleted='0' ORDER BY ag.`grid_level` ) parentTmp LEFT JOIN area_grid ag2 ON ag2.id=parentTmp.gridParent ) grandParentTmp LEFT JOIN area_grid ag3 ON ag3.id=grandParentTmp.gridGrandParent ) grandGrandParentTmp ON grandGrandParentTmp.gridId=ci.`grid` WHERE ci.is_deleted='0'
AND ci.is_finished=#{isFinish}
) result )countResult WHERE zfqp IS NOT NULL GROUP BY zfqp,zfqpName,zfqpLevel

SELECT cyear,cmonth,mapInfoCount,mapInfo FROM (
    SELECT YEAR(ci.occur_time) cyear,MONTH(ci.`occur_time`) cmonth,COUNT(ci.map_info) mapInfoCount,ci.map_info mapInfo
    FROM case_info ci
    WHERE ci.`map_info`IS NOT NULL AND ci.`map_info`!=''
    AND ci.occur_time IS NOT NULL AND ci.occur_time != ''

        AND ci.case_level=#{caseInfo.caseLevel}


        AND ci.source_type=#{caseInfo.sourceType}


        AND ci.grid=45


        AND ci.`occur_time` >= #{startDate}
        AND ci.`occur_time` <= #{endDate}

    GROUP BY ci.map_info,cyear,cmonth
) resultTmp

SELECT robj.`id` regulaObjectId, ptTmp1.pTotal patrolCount, IF(pCountWithProblem IS NOT NULL,pCountWithProblem,'0') pCountWithProblem, robj.`obj_name` objName,robj.map_info mapInfo FROM (
SELECT pt3.`regula_object_id` regulaObjectId,pt3.`id` patrolTaskId,pt3.`source_task_id`, spee.`spe_name`, ci.`case_title`,ci.`id` FROM special_event spee LEFT JOIN patrol_task pt3 ON spee.id=pt3.`source_task_id` LEFT JOIN case_info ci ON (ci.`source_code`=pt3.`id` AND ci.`source_type`=#{queryData.caseSourceType})
and pt3.`source_type`=#{queryData.patrolTaskSourceType}
AND pt3.`source_task_id` = #{queryData.sourceTaskId}
AND pt3.`crt_time` >=#{queryData.startTime} AND pt3.`crt_time` <=#{queryData.endTime}
AND pt3.`regula_object_type_id`=#{queryData.regulaObjectTypeId}
AND pt3.`regula_object_id` in (
#{item}
)
) spept LEFT JOIN (
SELECT COUNT(pt.`regula_object_id`) pTotal,pt.`regula_object_id` regulaObjectId FROM patrol_task pt GROUP BY pt.`regula_object_id` ) ptTmp1 ON ptTmp1.regulaObjectId=spept.regulaObjectId LEFT JOIN (
SELECT COUNT(pt2.regula_object_id) pCountWithProblem,pt2.regula_object_id regulaObjectId FROM patrol_task pt2 WHERE pt2.is_problem='1' GROUP BY pt2.regula_object_id ) ptTmp2 ON spept.regulaObjectId=ptTmp2.regulaObjectId LEFT JOIN regula_object robj ON robj.`id`=ptTmp1.regulaObjectId GROUP BY spept.regulaObjectId,ptTmp1.pTotal,pCountWithProblem

SELECT regula_obj_list regulaObjId, count(id) count FROM case_info WHERE is_deleted = '0' AND regula_obj_list IN
#{objId}
GROUP BY regula_obj_list

select result01._grid grid,result01.stateTodo,result01.stateFinish,result01.stateStop,overtimeCount, ag.`grid_name` gridName,ag.grid_level gridLevel,ag.grid_code gridCode,ag.grid_parent gridParent, parentAg.grid_code gridParentCode,parentAg.grid_name gridParentName from (
SELECT _grid,overtimeCount, MAX(CASE is_finished WHEN '0' THEN _count ELSE 0 END ) stateTodo, MAX(CASE is_finished WHEN '1' THEN _count ELSE 0 END ) stateFinish, MAX(CASE is_finished WHEN '2' THEN _count ELSE 0 END ) stateStop FROM ( SELECT is_finished,COUNT(_grid) _count,_grid FROM ( SELECT id,is_finished, CASE
WHEN grid IN (${item.gridChildrenSet}) THEN ${item.parentId}
ELSE '-1' END _grid FROM case_info ci2
ci2.`is_deleted`='0'
and event_type_list=#{caseInfo.eventTypeList}
and source_type=#{caseInfo.sourceType}
and case_level=#{caseInfo.caseLevel}
and occur_time BETWEEN #{startTime} AND #{endTime}
and biz_list=#{caseInfo.bizList}
) tmp1 GROUP BY tmp1._grid,is_finished ORDER BY _grid,is_finished ) tmp2
left join ( SELECT grid, COUNT(id) overtimeCount FROM case_info WHERE dead_line IS NOT NULL AND ( ( is_finished = 0 AND NOW() > dead_line ) OR ( is_finished IN (1, 2) AND finish_time > dead_line ) ) GROUP BY grid ) overtimeTmp on overtimeTmp.grid=tmp2._grid
WHERE _grid!='-1' GROUP BY _grid,overtimeCount )result01
LEFT JOIN area_grid ag ON ag.`id`=result01._grid
LEFT JOIN area_grid parentAg ON parentAg.id=ag.grid_parent

SELECT superCaseInfo.id,superCaseInfo.`case_title` caseTitle,superCaseInfo.`source_type` sourceType,superCaseInfo.`is_finished` isFinished,superCaseInfo.`event_type_list` eventTypeList,
ei.`finish_time` exeTime,superCaseInfo.`finish_time` finishTime
FROM
case_info superCaseInfo
LEFT JOIN execute_info ei ON ei.`case_id`=superCaseInfo.`id`



			superCaseInfo.id IN (
			# 接收数
			SELECT ei01.`case_id` ei01CaseId
			FROM execute_info ei01
			INNER JOIN case_info ci01 ON ci01.`id`=ei01.`case_id`
			WHERE ci01.`crt_time` BETWEEN #{startTime} AND #{endTime}
			AND ei01.`exe_person`=#{userId}
			)


			OR
			(
			superCaseInfo.`source_code` IN (
			# 上报数
			SELECT pk01.`id` pkId
			FROM patrol_task pk01
			WHERE pk01.`crt_time` BETWEEN #{startTime} AND #{endTime}
			AND pk01.`crt_user_id`=#{userId}
			)
			AND superCaseInfo.`source_type`='root_biz_eventType_check'
			)


			OR (
			superCaseInfo.id IN (
			# 完成数
			SELECT ei01.`case_id` ei01CaseId
			FROM execute_info ei01
			INNER JOIN case_info ci01 ON ci01.`id`=ei01.`case_id`
			WHERE ci01.`crt_time` BETWEEN #{startTime} AND #{endTime}
			AND ei01.`exe_person`=#{userId}
			AND ci01.`is_finished`='1'
			)
			)


			OR (
			superCaseInfo.`id` IN (
			# 退回数
			SELECT ei01.`case_id` ei01CaseId
			FROM execute_info ei01
			INNER JOIN case_info ci01 ON ci01.`id`=ei01.`case_id`
			WHERE ci01.`crt_time` BETWEEN #{startTime} AND #{endTime}
			AND ei01.`exe_person`=#{userId}
			AND ci01.`is_finished`='2'
			)
			)


			superCaseInfo.id IN (
			# 接收数
			SELECT ei01.`case_id` ei01CaseId
			FROM execute_info ei01
			INNER JOIN case_info ci01 ON ci01.`id`=ei01.`case_id`
			WHERE ci01.`crt_time` BETWEEN #{startTime} AND #{endTime}
			AND ei01.`exe_person`=#{userId}
			)
			OR
			(
			superCaseInfo.`source_code` IN (
			# 上报数
			SELECT pk01.`id` pkId
			FROM patrol_task pk01
			WHERE pk01.`crt_time` BETWEEN #{startTime} AND #{endTime}
			AND pk01.`crt_user_id`=#{userId}
			)
			AND superCaseInfo.`source_type`='root_biz_eventType_check'
			)
			OR (
			superCaseInfo.id IN (
			# 完成数
			SELECT ei01.`case_id` ei01CaseId
			FROM execute_info ei01
			INNER JOIN case_info ci01 ON ci01.`id`=ei01.`case_id`
			WHERE ci01.`crt_time` BETWEEN #{startTime} AND #{endTime}
			AND ei01.`exe_person`=#{userId}
			AND ci01.`is_finished`='1'
			)
			)
			OR (
			superCaseInfo.`id` IN (
			# 退回数
			SELECT ei01.`case_id` ei01CaseId
			FROM execute_info ei01
			INNER JOIN case_info ci01 ON ci01.`id`=ei01.`case_id`
			WHERE ci01.`crt_time` BETWEEN #{startTime} AND #{endTime}
			AND ei01.`exe_person`=#{userId}
			AND ci01.`is_finished`='2'
			)
			)



ORDER BY superCaseInfo.id DESC

select
ci.id,
ci.case_title caseTitle,
ci.source_type sourceType,
ci.is_finished isFinished,
ci.event_type_list eventTypeList,
ci.upd_time exeTime,
ci.finish_time  finishTime
FROM case_info ci
WHERE 1 = 1

	and ci.id in

		 #{caseIds}




	ci.id = #{userId}




		and ci.source_code IN (
		SELECT DISTINCT(pt.id)
		from patrol_task pt
		where pt.is_problem = '0'

			and   pt.crt_user_id = #{userId}


			and #{startTime} <= pt.crt_time and pt.crt_time <= #{endTime}

		)



		and ci.source_code IN (
		SELECT
		DISTINCT(pt.id)
		from patrol_task pt
		where pt.is_problem = '1'

			and    pt.crt_user_id = #{userId}


			and #{startTime} <= pt.crt_time and pt.crt_time <= #{endTime}

		)



		and ci.source_code IN (
		SELECT
		DISTINCT(pt.id)
		from patrol_task pt
		left join  case_info ci2 on pt.crt_user_id = ci2.crt_user_id
		where pt.status = 'root_biz_partolTaskT_stop'

			and pt.crt_user_id = #{userId}


			and #{startTime} <= pt.crt_time and pt.crt_time <= #{endTime}

		)



		and ci.id IN (
		SELECT
		DISTINCT(ci2.id)
		from case_info ci2
		where
		ci2.crt_time = ci2.upd_time  and ci2.is_finished = '1'

			and  ci2.crt_user_id = #{userId}


			and #{startTime} <= ci.crt_time and ci.crt_time <= #{endTime}

		)



		and ci.id in (
			select task.PROC_BIZID
			from scp_workflow.scp_wf_proc_task task
			where  FROM_UNIXTIME(task.PROC_TASK_COMMITTIME,'%Y-%m-%d %H:%i:%s')between #{startTime} and #{endTime} and task.PROC_CTASKCODE = 'processing' and task.PROC_PTASKCODE = 'gridMemberNode'

			and task.PROC_TASK_ASSIGNEE = #{userId}

			)



		and ci.id IN (
			select task.PROC_BIZID
			from scp_workflow.scp_wf_proc_task task
			where FROM_UNIXTIME(task.PROC_TASK_COMMITTIME,'%Y-%m-%d %H:%i:%s') between #{startTime} and #{endTime} and task.PROC_CTASKCODE = 'processing' and task.PROC_PTASKCODE = 'toFinish'

			and task.PROC_TASK_ASSIGNEE = #{userId}

			)



	and #{startTime} <= ci.crt_time and ci.crt_time <= #{endTime}


and ci.is_deleted = '0'
ORDER BY  ci.crt_time desc

select DISTINCT(ci.id) caseId
from  case_info ci
where
1 = 1

	and ci.crt_user_id =  #{userId}

and ci.crt_time between #{startTime} and #{endTime}
