SELECT proc.PROC_ID procId, proc.PROC_KEY procKey, proc.PROC_NAME procName, proc.PROC_INST_ID procInstId, proc.PROC_BIZID procBizid, proc.PROC_BIZTYPE procBiztype, proc.PROC_ORGCODE procOrgcode, proc.PROC_MEMO procMemo, task.PROC_TASK_STATUS procTaskStatus, task.PROC_TASK_PROPERTIES procTaskProperties, CASE WHEN task.PROC_TASK_STATUS = '1' THEN '待签收' WHEN task.PROC_TASK_STATUS = '2' THEN '待处理' WHEN task.PROC_TASK_STATUS = '3' THEN '已处理' WHEN task.PROC_TASK_STATUS = '4' THEN '已终止' ELSE '未知' END procTaskStatusName, task.ID id, task.PROC_CTASKID procCtaskId, task.PROC_CTASKCODE procCtaskcode, task.PROC_CTASKNAME procCtaskname, task.PROC_TASK_COMMITTER procTaskCommitter, task.PROC_APPROVEURL procApproveurl, task.PROC_SELF_PROPERTIES procSelfProperties, IF ( task.PROC_TASK_STATUS = '1', task.PROC_TASK_GROUP, task.PROC_TASK_ASSIGNEE ) procTaskAssignee, task.PROC_LICENSOR procLicensor, FROM_UNIXTIME(task.PROC_TASK_COMMITTIME, '%Y/%m/%d %H:%i:%s') procTaskCommittime, FROM_UNIXTIME(task.PROC_TASK_ASSIGNTIME, '%Y/%m/%d %H:%i:%s') procTaskAssigntime, task.PROC_REFUSETASK procRefusetask FROM SCP_WF_PROC proc JOIN SCP_WF_PROC_TASK task ON proc.PROC_INST_ID = task.PROC_INST_ID LEFT JOIN SCP_WF_PROC_DELEGATE delegate ON delegate.PROC_INST_ID = proc.PROC_INST_ID AND delegate.PROC_MANDATARY = #{procTaskUser} AND delegate.PROC_LICENSE_ISVALID = '1' WHERE proc.PROC_STATUS = '0' AND task.PROC_TASK_STATUS IN ('1', '2') AND ( ( task.PROC_TASK_STATUS = '1' AND (
task.PROC_TASK_GROUP like "%"#{item}"%" OR
task.PROC_TASK_GROUP = '' OR delegate.PROC_MANDATARY=#{procTaskUser} and task.PROC_TASK_GROUP=delegate.PROC_LICENSOR_ROLE ) ) OR ( task.PROC_TASK_STATUS = '2' AND task.PROC_TASK_ASSIGNEE = #{procTaskUser} ) ) AND ( task.PROC_DATAPERMISSION = '0' OR task.PROC_DATAPERMISSION = '1' AND proc.PROC_ORGCODE = #{procOrgCode} OR task.PROC_DATAPERMISSION = '2' AND SUBSTR(proc.PROC_ORGCODE, 1, LENGTH(#{procOrgCode}))=#{procOrgCode} and LENGTH(proc.PROC_ORGCODE) > LENGTH(#{procOrgCode}) OR task.PROC_DATAPERMISSION = '3' AND SUBSTR(proc.PROC_ORGCODE, 1, LENGTH(#{procOrgCode}))=#{procOrgCode} OR task.PROC_DATAPERMISSION = '9' AND task.PROC_APPOINT_USERS = #{procTaskUser}) AND (task.PROC_TENANTPERMISSION = '0' OR task.PROC_TENANTPERMISSION = '1' and proc.PROC_TENANT_ID = #{procTenantId}) AND (task.PROC_ORGPERMISSION = '0' OR task.PROC_ORGPERMISSION = '1' and task.PROC_ORGCODE = #{procOrgCode} OR task.PROC_ORGPERMISSION = '2' and (task.PROC_ORGCODE = #{procOrgCode} or
task.PROC_ORGCODE in
#{item}
) OR task.PROC_ORGPERMISSION = '3' and
task.PROC_ORGCODE in
#{item}
OR task.PROC_ORGPERMISSION = '9' AND task.PROC_APPOINT_USERS = #{procTaskUser}) AND ( task.PROC_DEPTPERMISSION = '0' OR task.PROC_DEPTPERMISSION = '1' and task.PROC_DEPART_ID = #{procDeptId} ) AND (task.PROC_SELFPERMISSION1 = '0' OR task.PROC_SELFPERMISSION1 = '1' and task.PROC_SELFDATA1 in (${procSelfPermissionData1})) AND (task.PROC_SELFPERMISSION2 = '0' OR task.PROC_SELFPERMISSION2 = '1' and task.PROC_SELFDATA2 = #{procSelfPermissionData2}) AND (task.PROC_SELFPERMISSION3 = '0' OR task.PROC_SELFPERMISSION3 = '1' and task.PROC_SELFDATA3 = #{procSelfPermissionData3}) AND (task.PROC_SELFPERMISSION4 = '0' OR task.PROC_SELFPERMISSION4 = '1' and task.PROC_SELFDATA4 = #{procSelfPermissionData4}) AND (task.PROC_SELFPERMISSION5 = '0' OR task.PROC_SELFPERMISSION5 = '1' and task.PROC_SELFDATA5 = #{procSelfPermissionData5}) AND task.PROC_KEY=#{prockey}
and proc.PROC_NAME LIKE "%"#{procName}"%"
and task.PROC_CTASKNAME = #{procCtaskname}
and task.PROC_CTASKCODE in (${procCtaskcode})
and proc.PROC_MEMO LIKE "%"#{procMemo}"%"
and task.PROC_TASK_STATUS = #{procTaskStatus}
and proc.PROC_BIZID LIKE "%"#{procBizid}"%"
AND proc.PROC_CREATETIME >= #{procCreateTimeStart}
AND proc.PROC_CREATETIME <= #{procCreateTimeEnd}
AND task.PROC_TASK_COMMITTIME >= #{procTaskCommitTimeStart}
AND task.PROC_TASK_COMMITTIME <= #{procTaskCommitTimeEnd}
ORDER BY task.PROC_TASK_COMMITTIME ASC

SELECT DISTINCT proc.PROC_CREATETIME, myproc.proc_inst_id procInstId, myproc.PROC_TASKID procTaskid, CASE myproc.PROC_USER_TYPE WHEN '1' THEN '流程创建者' WHEN '2' THEN '流程参与者' WHEN '3' THEN '流程订阅者' WHEN '4' THEN '流程委托人' WHEN '5' THEN '流程受托人' ELSE '未知' END AS procUserType, task.PROC_CTASKCODE procTaskcode, task.PROC_CTASKNAME procTaskname, proc.proc_bizid procBizId, proc.PROC_BIZTYPE procBiztype, proc.PROC_ORGCODE procOrgcode, proc.proc_name procName, FROM_UNIXTIME(proc.PROC_CREATETIME, '%Y/%m/%d %H:%i:%s') procCreatetime, FROM_UNIXTIME(proc.PROC_ENDTIME, '%Y/%m/%d %H:%i:%s') procEndtime, proc.PROC_MEMO procMemo, proc.PROC_STATUS procStatus, CASE proc.PROC_STATUS WHEN '0' THEN '审批中' WHEN '3' THEN '已暂停' WHEN '4' THEN '已终止' WHEN '5' THEN '已删除' WHEN '6' THEN '已完成' WHEN '7' THEN '已取消' ELSE '未知' END AS procStatusName, myproc.PROC_DISPLAYURL procDisplayurl, task.PROC_TASK_COMMITTER procTaskCommitter, IF (task.PROC_TASK_STATUS = '1', task.PROC_TASK_GROUP, task.PROC_TASK_ASSIGNEE) procTaskAssignee, task.PROC_LICENSOR procLicensor, FROM_UNIXTIME(task.PROC_TASK_COMMITTIME, '%Y/%m/%d %H:%i:%s') procTaskCommittime, FROM_UNIXTIME(task.PROC_TASK_ASSIGNTIME, '%Y/%m/%d %H:%i:%s') procTaskAssigntime, FROM_UNIXTIME(task.PROC_TASK_ENDTIME, '%Y/%m/%d %H:%i:%s') procTaskEndime, task.PROC_TASK_STATUS procTaskstatus FROM SCP_wf_my_proc myproc LEFT JOIN SCP_wf_proc proc ON myproc.proc_inst_id = proc.proc_inst_id LEFT JOIN SCP_wf_proc_task task ON task.proc_inst_id = myproc.proc_inst_id AND task.PROC_CTASKID = myproc.PROC_TASKID WHERE myproc.PROC_USER = #{procTaskUser,jdbcType=VARCHAR} and proc.PROC_TENANT_ID = #{procTenantId,jdbcType=VARCHAR}
AND proc.PROC_STATUS = #{procStatus,jdbcType=VARCHAR}
AND proc.PROC_CREATETIME >= #{procCreateTimeStart}
AND proc.PROC_CREATETIME <= #{procCreateTimeEnd}
AND proc.PROC_ENDTIME >= #{procFinishedTimeStart}
AND proc.PROC_ENDTIME <= #{procFinishedTimeEnd}
AND proc.proc_bizid LIKE CONCAT('%', #{procBizid},'%')
AND proc.PROC_NAME LIKE CONCAT('%', #{procName},'%')
AND proc.PROC_MEMO LIKE CONCAT('%', #{procMemo},'%')
ORDER BY proc.PROC_CREATETIME DESC

SELECT count(DISTINCT myproc.PROC_TASKID)
FROM SCP_wf_my_proc myproc
LEFT JOIN SCP_wf_proc proc ON myproc.proc_inst_id = proc.proc_inst_id
WHERE
myproc.PROC_USER = #{procTaskUser,jdbcType=VARCHAR}
and proc.PROC_TENANT_ID = #{procTenantId,jdbcType=VARCHAR}

	AND proc.PROC_STATUS = #{procStatus,jdbcType=VARCHAR}



	AND proc.PROC_CREATETIME >= #{procCreateTimeStart}



	AND proc.PROC_CREATETIME <= #{procCreateTimeEnd}



	AND proc.PROC_ENDTIME >= #{procFinishedTimeStart}



	AND proc.PROC_ENDTIME <= #{procFinishedTimeEnd}



	AND proc.proc_bizid LIKE CONCAT('%', #{procBizid},'%')



	AND proc.PROC_NAME LIKE CONCAT('%', #{procName},'%')



	AND proc.PROC_MEMO LIKE CONCAT('%', #{procMemo},'%')

ORDER BY proc.PROC_CREATETIME DESC

SELECT proc.PROC_INST_ID procInstId, proc.PROC_NAME procName, proc.PROC_BIZID procBizId, proc.PROC_BIZTYPE procBiztype, proc.PROC_MEMO procMemo, proc.PROC_ORGCODE procOrgcode, task.PROC_CTASKID procTaskid, task.PROC_CTASKCODE procTaskcode, task.PROC_CTASKNAME procTaskname, proc.PROC_DISPLAYURL procDisplayurl, task.PROC_TASK_COMMITTER procTaskCommitter, IF ( task.PROC_TASK_STATUS = '1', task.PROC_TASK_GROUP, task.PROC_TASK_ASSIGNEE ) procTaskAssignee, task.PROC_LICENSOR procLicensor, FROM_UNIXTIME( proc.PROC_CREATETIME, '%Y/%m/%d %H:%i:%s' ) procCreatetime, FROM_UNIXTIME( task.PROC_TASK_COMMITTIME, '%Y/%m/%d %H:%i:%s' ) procTaskCommittime, FROM_UNIXTIME( task.PROC_TASK_ASSIGNTIME, '%Y/%m/%d %H:%i:%s' ) procTaskAssigntime, proc.PROC_STATUS procStatus, CASE proc.PROC_STATUS WHEN '0' THEN '审批中' WHEN '3' THEN '已暂停' ELSE '未知' END AS procStatusName FROM SCP_WF_PROC proc LEFT JOIN SCP_WF_PROC_TASK task ON ( proc.PROC_INST_ID = task.PROC_INST_ID AND task.PROC_TASK_STATUS IN (1, 2) ) WHERE proc.PROC_STATUS IN (0, 3) and proc.PROC_TENANT_ID = #{procTenantId, jdbcType=VARCHAR}
AND proc.PROC_STATUS = #{procStatus,jdbcType=VARCHAR}
AND proc.PROC_CREATETIME >= #{procCreateTimeStart}
AND proc.PROC_CREATETIME <= #{procCreateTimeEnd}
AND task.PROC_TASK_COMMITTIME >= #{procCommitTimeStart}
AND task.PROC_TASK_COMMITTIME <= #{procCommitTimeEnd}
AND proc.proc_bizid LIKE CONCAT('%', #{procBizid},'%')
AND proc.PROC_NAME LIKE CONCAT('%', #{procName},'%')
AND proc.PROC_MEMO LIKE CONCAT('%', #{procMemo},'%')
ORDER BY proc.PROC_CREATETIME ASC, task.PROC_TASK_COMMITTIME ASC

