SELECT delegate.PROC_INST_ID procInstId, proc.PROC_NAME procName, proc.PROC_BIZID procBizId, proc.PROC_BIZTYPE procBiztype, proc.PROC_MEMO procMemo, proc.PROC_ORGCODE procOrgcode, proc.PROC_DISPLAYURL procDisplayurl, delegate.PROC_LICENSOR procLicensor, delegate.PROC_MANDATARY procMandatary, FROM_UNIXTIME( delegate.PROC_LICENSE_TIME, '%Y/%m/%d %H:%i:%s' ) procLicenseTime, FROM_UNIXTIME( proc.PROC_CREATETIME, '%Y/%m/%d %H:%i:%s' ) procCreatetime, FROM_UNIXTIME( proc.PROC_ENDTIME, '%Y/%m/%d %H:%i:%s' ) procEndtime, proc.PROC_STATUS procStatus, CASE proc.PROC_STATUS WHEN '0' THEN '审批中' WHEN '3' THEN '已暂停' WHEN '4' THEN '已终止' WHEN '5' THEN '已删除' WHEN '6' THEN '已完成' WHEN '7' THEN '已取消' ELSE '未知' END AS procStatusName FROM SCP_wf_proc_delegate delegate LEFT JOIN SCP_wf_proc proc ON proc.PROC_INST_ID = delegate.PROC_INST_ID WHERE delegate.PROC_LICENSE_ISVALID='1' and proc.PROC_TENANT_ID = #{procTenantId,jdbcType=VARCHAR} AND (proc.PROC_ORGCODE = #{procOrgCode} or
proc.PROC_ORGCODE in
#{item}
)
AND proc.PROC_STATUS = #{procStatus,jdbcType=VARCHAR}
AND proc.PROC_CREATETIME >= #{procCreateTimeStart}
AND proc.PROC_CREATETIME <= #{procCreateTimeEnd}
AND proc.PROC_ENDTIME >= #{procFinishedTimeStart}
AND proc.PROC_ENDTIME <= #{procFinishedTimeEnd}
AND proc.proc_bizid LIKE CONCAT('%', #{procBizid},'%')
AND proc.PROC_NAME LIKE CONCAT('%', #{procName},'%')
AND proc.PROC_MEMO LIKE CONCAT('%', #{procMemo},'%')
ORDER BY proc.PROC_CREATETIME DESC, delegate.PROC_LICENSE_TIME ASC

SELECT proc.proc_inst_id procInstId, proc.proc_bizid procBizId, proc.PROC_BIZTYPE procBiztype, proc.PROC_ORGCODE procOrgcode, proc.proc_name procName, FROM_UNIXTIME(proc.PROC_CREATETIME, '%Y/%m/%d %H:%i:%s') procCreatetime, FROM_UNIXTIME(proc.PROC_ENDTIME, '%Y/%m/%d %H:%i:%s') procEndtime, proc.PROC_MEMO procMemo, proc.PROC_DISPLAYURL procDisplayurl, proc.PROC_STATUS procStatus, CASE proc.PROC_STATUS WHEN '0' THEN '审批中' WHEN '3' THEN '已暂停' WHEN '4' THEN '已终止' WHEN '5' THEN '已删除' WHEN '6' THEN '已完成' WHEN '7' THEN '已取消' ELSE '未知' END AS procStatusName, task.PROC_CTASKID procTaskid, task.PROC_CTASKCODE procTaskcode, task.PROC_CTASKNAME procTaskname, task.PROC_TASK_COMMITTER procTaskCommitter, IF (task.PROC_TASK_STATUS = '1', task.PROC_TASK_GROUP, task.PROC_TASK_ASSIGNEE) procTaskAssignee, task.PROC_LICENSOR procLicensor, FROM_UNIXTIME(task.PROC_TASK_COMMITTIME, '%Y/%m/%d %H:%i:%s') procTaskCommittime, FROM_UNIXTIME(task.PROC_TASK_ASSIGNTIME, '%Y/%m/%d %H:%i:%s') procTaskAssigntime, task.PROC_TASK_STATUS procTaskstatus FROM SCP_wf_proc proc LEFT JOIN SCP_wf_proc_task task ON task.proc_inst_id = proc.proc_inst_id AND task.PROC_TASK_STATUS IN ('1', '2') WHERE proc.PROC_TENANT_ID = #{procTenantId,jdbcType=VARCHAR} AND (proc.PROC_ORGCODE = #{procOrgCode} or
proc.PROC_ORGCODE in
#{item}
)
AND proc.PROC_STATUS = #{procStatus,jdbcType=VARCHAR}
AND proc.PROC_CREATETIME >= #{procCreateTimeStart}
AND proc.PROC_CREATETIME <= #{procCreateTimeEnd}
AND proc.PROC_ENDTIME >= #{procFinishedTimeStart}
AND proc.PROC_ENDTIME <= #{procFinishedTimeEnd}
AND proc.proc_bizid LIKE CONCAT('%', #{procBizid},'%')
AND proc.PROC_NAME LIKE CONCAT('%', #{procName},'%')
AND proc.PROC_MEMO LIKE CONCAT('%', #{procMemo},'%')
ORDER BY proc.PROC_CREATETIME DESC, task.PROC_TASK_COMMITTIME DESC
